INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (1, '谷丙转氨酶 ', 'ALT ', NULL, 'U/L ', '增高：常见于急慢性肝炎，药物性肝损伤，脂肪肝，肝硬化，心梗，胆道疾病等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (2, '白细胞计数', 'WBC', NULL, '个/L', '白细胞高．可以积极的抗炎治疗．WBC一般代表的是白细胞计数（White blood cell count）。白细胞稍微高了一点点，最常见原因是细菌感染.白细胞低说明免疫低下，极易感染各种病毒和细菌。', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (3, '红细胞计数', 'RBC', NULL, '/L', '正常情况下，红细胞的生成和破坏处于动态平衡，因而血液中红细胞的数量及质量保持相对稳定。无论何种原因造成的红细胞生成与破坏的失常，都会引起红细胞在数量上或质量上的改变，从而导致疾病的发生。', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (4, '血小板', '', NULL, '/L', '血小板减少是引起出血时间延长，严重损伤或在激状态可发生出血', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (5, '血红蛋白', 'HGB', NULL, 'g/L', '病理性增加
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (6, '红细胞比容', 'HCT', NULL, '%', '血细胞比容测定的临床意义基本同红细胞计数或血红蛋白测定，常用作贫血诊断和分类的指标。还可用于临床决定病人是否需要补液的实验检查依据。', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (7, '凝血酶原时间', 'PT', NULL, '', '主要反映外源性凝血系统状况，其中INR常用于监测口服抗凝剂。延长见于先天性凝血因子ⅡⅤⅦⅩ缺乏及纤维蛋白原缺乏，后天凝血因子缺乏主要见于维生素K缺乏、严重的肝脏疾病、纤溶亢进、DIC、口服抗凝剂等；缩短见于血液高凝状态和血栓性疾病等；', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (8, '活化部分凝血活酶时间', 'APTT', NULL, '', '主要反映内源性凝血系统状况，常用于监测肝素用量。增高见于血浆因子Ⅷ、因子Ⅸ和因子XI水平减低：如血友病A、血友病B及因子XI缺乏症；降低见于高凝状态：如促凝物质进入血液及凝血因子的活性增高等情况；
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (9, '凝血酶时间', 'TT', NULL, '', '主要反映纤维蛋白原转为纤维蛋白的时间。增高见于DIC纤溶亢进期，低（无）纤维蛋白原血症，异常血红蛋白血症，血中纤维蛋白（原）降解产物（FDPs）增高；降低无临床意义。', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (10, '纤维蛋白原', 'FIB', NULL, 'g/L', '主要反映纤维蛋白原的含量。增高见于急性心肌梗死；减低见于DIC消耗性低凝溶解期、原发性纤溶症、重症肝炎、肝硬化；', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (11, '谷草转氨酶 ', 'AST ', NULL, 'U/L ', '增高：常见于心梗，急慢性肝炎，中毒性肝炎，心功能不全，皮肌炎等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (12, '总胆红质 ', 'TBIL ', NULL, 'mg/dl', '增高：原发生胆汁性肝硬化急性黄疸型肝炎，慢性活动期肝炎，病毒性肝炎。肝硬化，溶血性黄疸，新生儿黄疸，胆石症等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (13, '直接胆红质 ', 'DBIL ', NULL, 'mg/dl', '增高：常见于阻塞性黄疸，肝癌，胰头癌，胆石症等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (14, '直接胆红素', 'LBIL', NULL, 'μmol/L', '增高：主要见于阻塞性黄疸、肝细胞性黄疸、肝癌、胰头癌、胆石症、胆管癌等。', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (15, '间接胆红质 ', 'IBIL ', NULL, 'mg/dl ', '增高：见于溶血性黄疸，新生儿黄疸，血型不符的输血反应 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (16, '总蛋白 ', 'TP ', NULL, 'mg/dl', '增高：常见于高度脱水症（如腹泄、沤吐，休克，高热）及多发性骨髓瘤。 降低：常见于恶性肿瘤，重症结核，营养及吸收障碍，肝硬化，肾病综合症，烧伤，失血。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (17, '白蛋白 ', 'ALB ', NULL, 'g/dl ', '增高：常见于严重失水而导致血浆浓缩，使白蛋白浓度上升。 降低：基本与总蛋白相同，特别是肝脏，肾脏疾病更为明显。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (18, '球蛋白', 'GLB', NULL, '克/分升', '球蛋白偏高通常是因为机体受到外来病毒的侵染，免疫系统就会来对抗外来病毒，从而导致球蛋白出现增高这种症状。', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (19, '碱性磷酸酶 ', 'ALP ', NULL, 'U/L ', '增高：常见于肝癌，肝硬化，阻塞性黄疸，急慢性黄疸型肝炎，骨细胞瘤，骨折及少年儿童。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (20, '转肽酶 ', 'GGT ', NULL, 'U/L ', '增高：常见于原发性或转移性肝癌，急性肝炎，慢性肝炎活动期，肝硬化，急性胰腺炎及心力衰竭等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (21, '胆汁酸 ', 'TBA ', NULL, 'umol/L ', '增高：急慢性肝炎，肝硬化，阻塞性黄疸及药物引起肝损害时。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (22, '铁 ', 'TRON ', NULL, 'u/L ', '增高：见于再生障碍性贫血，溶血性贫血，巨幼细胞性贫血，急性肝炎，Vtb缺乏症。 降低：见于缺铁性贫血，肝硬化，长期失血，铁吸收障碍。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (23, '肌酐 ', 'CRE ', NULL, 'mg/dl ', '增高：见于严重肾功能不全，各种肾障碍，肢端肥大症等。 降低：见于肌肉量减少（如营养不良，高龄者），多尿。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (24, '尿酸 ', 'URIC ', NULL, 'mg/dl ', '增高：见于痛风，子痫，白血病，红细胞增多症，多发性骨髓瘤，急慢性肾小球肾炎。 降低：见于恶性贫血及肾上腺皮质激素等药物治疗后。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (25, '血糖 ', 'GLU ', NULL, 'mg/dl ', '增高：某些生理因素（情绪紧张，饭后1-2小时）及注射肾上腺素后，病理性增高见于各种糖尿病，慢性胰腺炎，心梗，甲亢，垂体前叶嗜酸性细胞瘤，颅内出血，颅外伤等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (26, '糖化血红蛋白', 'HbA1C', NULL, '%', '', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (27, '尿素', 'UREA', NULL, 'mmol/L', '', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (28, '尿肌酐', 'Cr', NULL, 'mmol/d', '增高：见于肢端肥大症、巨人症、糖尿病、感染、甲状腺功能减低、进食肉类、运动、摄入药物（如维生素C、左旋多巴、甲基多巴等）。
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (29, '尿素氮 ', 'BUN ', NULL, 'mg/dl ', '增高：常见于高蛋白饮食，糖尿病，重症肝病，高热，轻度肾功能低下，高血压，痛风，多发性骨髓瘤尿路闭塞，术后无尿，尿毒症前期，肝硬化，严重肾功能衰竭，尿毒症。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (30, '血尿酸和尿尿酸', 'UA', NULL, '微摩尔每升', '', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (102, '6.军团菌抗原检测 ', '  ', NULL, '', '军团菌感染 ', 1, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (31, '肌酸激酶 ', 'CK ', NULL, 'U/L ', '增高：心梗4-6小时开始升高，18-36小时可达正常值的20-30倍，为最高峰，2-4天恢复正常，另外，病毒性心肌炎，皮肌炎，肌肉损伤，肌营养不良，心包炎，脑血管意外及心脏手术等都可以使CK增高。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (32, '乳酸脱氢酶 ', 'LDH ', NULL, 'U/L ', '增高：急性心肌梗塞发作后12-48小时开始升高，2-4天可达高峰，8-9天恢复正常。另外，肝脏疾病恶性肿瘤可引起LDH增高。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (33, '羟丁酸脱氢酶 ', 'HBDH ', NULL, 'U/L ', '增高：作为急性心梗诊断的一个指标，与LDH大致相同，在急性心梗时此酶在血液中维持高值，可达2周左右。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (34, '钙 ', 'Ca ', NULL, 'mg/dl ', '增高：见于骨肿瘤，甲状旁腺机能亢进，急性骨萎缩，肾上腺皮脂功能减退及维生素D摄入过量等。 降低：常见于维生素D缺乏，佝楼病，软骨病，小儿手足抽搐症，老年骨质疏松，甲状旁腺功能减退，慢性肾炎，尿毒症，低钙饮食及吸收不良。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (35, '磷 ', 'IP ', NULL, 'mg/dl ', '增高：见于甲状旁腺机能减退，急慢性肾功能不全，尿毒症，骨髓瘤及骨折愈合期。 降低：见于甲亢，代谢性酸中毒佝楼病，软骨病，肾功能衰竭，长期腹泻及吸收不良。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (36, '胆固醇 ', 'TC ', NULL, 'mg/dl ', '意义：1.高脂蛋白血症与异常脂蛋白血症的诊断及分类；2.心，脑血管病的危险因素的判断；3.TC增高或过低可以是原发的营养因素或继发于某些疾病，如甲状腺病，肾病等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (37, '甘油三酯 ', 'TG ', NULL, 'mg/dl ', '增高：可由遗传，饮食因素或继发于某些疾病，如糖尿病，肾病等。 降低：见于甲亢，肾上腺皮质功能低下，肝实质性病变，原发性β脂蛋白缺乏及吸收不良。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (38, '血清胆碱酯酶', 'CHE', NULL, 'U/L', '(1) 病理性升高：肾病综合征、甲状腺功能亢进、糖尿病、脂肪肝、原发性家族性高CHE血症、血清CHE变异、原发性肝癌等。　(2) 病理性降低：重症肝炎、慢性肝炎活动型、肝硬化、肝脓肿、各种癌、低蛋白血症(营养不良、贫血、感染、皮肌炎、急性心肌梗死)、遗传性血清CHE异常症、有机磷中毒(轻度中毒降低30%、中度中毒降低50%、重度中毒降低70%)、溃疡性结肠炎、肾功能不全、天疱疮、烧伤等。', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (39, '高密度脂蛋白 ', 'HDL-C ', NULL, 'mg/dl ', '降低：见于脑血管病，冠心病，高TG血症，严重疾病或手术后，吸烟，缺少运动等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (40, '低密度脂蛋白 ', 'LDL-C ', NULL, 'mg/dl ', '同血清总胆固醇测定。当LDL-C值在3.36-4.14mmol/L时，为危险边缘；>4.14mmol/L为危险水平。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (41, '总胆固醇', 'TCHO', NULL, 'mmol/L', '1.增高：高胆固醇血症与动脉粥样硬化的形成有明确关系；降低血清胆固醇使冠心病的发病率降低及停止粥样斑块的进展。血清胆固醇水平受年龄、性别等影响。除家族性高胆固醇血症（FH）外、血清胆固醇增高多见于继发于肾病综合征、甲状腺功能减低、糖尿病和胆道梗阻等。
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (42, '载脂蛋白AⅠ', 'APoAⅠ', NULL, 'g/L', '降低：冠心病、糖尿病、肾病综合征、遗传性ApoA-Ⅰ缺乏症(Tangier病)、家族性低α-脂蛋白血症、鱼眼病、重度营养不良、肝炎活动期、肝功能低下。', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (43, '载脂蛋白B', ' APoB ', NULL, 'mg/dl ', '增多：成人>l.0克/升为轻度偏高；>1.2g/L为明显增高。增高常见于Ⅱ型高脂血症、胆汁淤积、冠心病、肾病、脑血管病、甲状腺功能低下、银屑病。　降低：常见于肝功能损害的实质性病变、甲状腺功能亢进、低β-脂蛋白血症。  ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (44, 'AP0AL ', 'AP0 AL ', NULL, 'mg/dl ', '降低：常见于高脂血症，冠心病及肝实质性病变。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (45, 'AP0 B ', 'AP0 B ', NULL, 'mg/dl ', '增高：常见于高脂血症，冠心病，银屑病。 降低：常见于肝实质性病变。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (46, '脂蛋白a', 'Lp-a', NULL, 'mmol/L', '增高：见于动脉粥样硬化性心脑血管病、急性心肌梗死、家族性高胆固醇血症、糖尿病、大动脉瘤及某些癌症等。　减低：见于肝脏疾病、酗酒、摄入新霉素等药物后。', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (47, '血氨 ', 'NH3 ', NULL, 'ug/dl', '增高：见于肝昏迷，重症肝炎，休克。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (48, '淀粉酶 ', 'AMY ', NULL, 'U/L', '增高：见于急慢性胰腺炎，胰腺癌，胆道疾病，胃穿孔，肠梗阻，腮腺炎，唾液腺炎等。 降低：见于肝脏疾病，如肝癌，肝硬化。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (49, '钾 ', 'K ', NULL, 'mmol/L', '增高：1.经口及静脉摄入增加。2.钾流入细胞外液；如严重溶血，感染烧伤，组织破坏，胰岛素缺乏。3.组织缺氧；心功能不全，呼吸障碍，休克。4.尿排泄障碍；肾功能衰竭及肾上腺皮质功能减退。5.毛地黄素大量服用。 降低：1.经口摄入减少。2.钾移入细胞内液；碱中毒及使用胰岛素后，IRI分泌增加。3.消化道钾丢失；频繁呕吐腹泻。4.尿钾丢失；肾小管性酸中毒。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (50, '纳 ', 'Na ', NULL, '', '增高：1.严重脱水，大量出汗，高烧，烧伤，糖尿病性多尿。2.肾上腺皮质功能亢进，原发及继发性醛固酮增多病。 降低：1.肾皮质功能不全，重症肾盂肾炎，糖尿病。2.胃肠道引流，呕吐及腹泻。3.肮利尿激素过多。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (51, '氯 ', 'Cl ', NULL, '', '增高：见于高钠血症，呼吸碱中毒，高渗性脱水，肾炎少尿及尿道梗塞。 降低：见于低钠血症，严重呕吐，腹泻，胃液胰液胆汁液大量丢失，肾功能减退及阿狄森氏病等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (52, '脑脊液生化糖 ', '  ', NULL, '', '脑脊液蛋白增高：见于感染性脑膜炎，脑脓肿，脊髓痨，脑血栓，脊髓灰白质炎，流行性脑炎，蛛网膜下出血，脑瘤出血及脑外伤等。 脑脊液蛋白降低：见于甲亢及良性颅内压增高症。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (53, '蛋白 ', '  ', NULL, '', '脑脊液糖增高：见于病毒性脑炎，乙型脑炎，脊髓灰质炎，脑肿瘤，脑水肿及糖尿病等。 脑脊液糖降低：见于化脓性脑膜炎，结核性脑膜炎，脑脓肿，流行性脑脊髓炎等。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (54, '肌钙蛋白I ', 'TNI ', NULL, 'ng/ml', '诊断心肌梗塞的灵敏指标，同时它又是判断不稳定心绞痛患者预后的一个重要指标。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (55, '肌钙蛋白T ', 'TNT ', NULL, 'ng/ml', '同TNI。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (56, 'CK-MB同工酶 ', 'CK-MB ', NULL, 'ng/ml', '是诊断心肌梗塞的重要指标，在急性心梗4小时开始上升，72小时后恢复正常。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (57, 'LDH同工酶 ', 'IS0-LDH ', NULL, '', '心梗：LDH1>LDH2；皮肌炎：LDH2>LDH3；肝炎：LDH4>LDH5；恶性肿瘤：LDH3明显增高；溶贫LDH1>LDH2。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (58, 'CK-MM同工酶 ', 'CK-MM ', NULL, '', '与CKMB的临床意义相似，而特异性稍低。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (59, '脂肪酶 ', 'Lipase ', NULL, 'U/L ', '与淀粉酶的临床意义相似，持续时间更长些。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (60, '胸水 ', '  ', NULL, '', '  ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (61, 'LDH（胸水） ', '  ', NULL, '', '恶性肿瘤时胸腹水LDH明显升高。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (62, 'CL（胸水） ', '  ', NULL, 'mmol/L ', '尿毒症病人脑脊液氯化物长高。降低：常见于结核性脑膜炎，化脓性脑膜炎。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (63, '氯（尿） ', '  ', NULL, 'mmol/L ', '一般情况下，尿钠与尿氯保持相对平衡，但连续服用NaCL或KCL 后，尿氯比尿钠高；相反，连服大量碱性钠盐，尿钠比尿氯高。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (64, '钾（尿） ', '  ', NULL, 'mmol/L ', '当使用利尿时，尿钾排泄增多，原发性醛固酮增高症人尿钾排泄增加。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (65, '钠（尿） ', '  ', NULL, 'mmol/L ', '判断是否有大量盐的丢失，判断呕吐，腹泻患者的电解质平衡，中枢神经系统疾病，脑出血，炎症，肿瘤，肾小管严重损害患者尿钠增多。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (66, '钙（尿） ', '  ', NULL, 'mmol/24h', '增高：见于甲状旁腺机能亢进。 降低：见于甲状旁腺机能减退，慢性肾功能不全，慢性腹泻。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (67, '磷（尿） ', '  ', NULL, 'mmol/24h', '增高：见于甲状旁腺机能亢进。 降低：见于甲状旁腺机能减退，肾病引起的尿毒症。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (68, '肌酐（尿） ', '  ', NULL, 'mmol/kg/24h ', '增高：见于破伤风，伤寒，皮肌炎。 降低：见于肾功能不全，白血症。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (69, '尿酸（尿） ', '  ', NULL, 'g/24h ', '  ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (70, '腺苷脱氨酸 ', 'ADA ', NULL, 'U/L ', '可作为结核病的辅助诊断指标；有文献报道良性胸水ADA偏低，癌性胸水正常或稍增高，而结核性胸膜炎明显增高，血清ADA测定可用于肝脏疾病的诊断。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (71, '血清蛋白电泳 ', 'A ', NULL, '', '白蛋白增高：同血清白蛋白。α1增高：肝硬化，肾病；α2增高：肾病，肝硬化，肝脓肿；β增高：高脂血症，阻塞黄疸，肝硬化；γ增高：慢性感染，肝硬化，肿瘤，多发性骨髓瘤。白蛋白降低：同血清白蛋白蛋白；α1降低：严重肝病；α2降低：严重肝病；β降低：严重肝病；γ降低，肾病。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (72, '乳酸 ', 'LACT ', NULL, 'mg/dl ', '增高：见于缺氧，酸中毒，休克，严重贫血，肺功能不全，肌肉痉挛，糖尿病，剧烈运动。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (73, '血液或尿液氨基酸分析 ', '  ', NULL, '', '体液游离氨基酸的水平及改变与某些疾病有关，如肝病，肝性脑病，肾病及某些先天性，代谢性疾病有关。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (74, '血浆同型半胱氨酸 ', 'HCY ', NULL, 'μmol/L ', '心脑血管病独立危险因素。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (75, '血浆叶酸测定 ', 'FA ', NULL, '', '诊断贫血指标，与HCY代谢有关 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (76, '血浆维生素B12 ', 'VB12 ', NULL, '', '诊断贫血指标，与HCY代谢有关。 ', 2, true, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (77, '血（尿）铜 ', 'Cu ', NULL, 'mg/dl ', '增高：见于恶性肿瘤，肝硬化，甲亢，结核等。 降低：见于先天性铜吸收及肝脏代谢障碍，肾病综合症及肝豆状核变性。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (78, '血（尿）锌 ', 'Zn ', NULL, 'mg/dl ', '增高：见于锌中毒，甲亢，X射线照射后。 降低：见于急慢性肝炎，肝硬化，肝瘤，心梗，贫血。儿童缺锌症，重症肌无力。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (79, '血镁 ', 'Mg ', NULL, 'mg/dl ', '增高：见于急慢性肾功能不全，甲状腺功能低下，严重脱水及糖尿病昏迷。 降低：见于甲亢，长期腹泻，呕吐，糖尿病酸中毒，原发性醛固酮症。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (80, '尿17-羟固醇 ', '17-OH ', NULL, 'mg/24h ', '增高：见于柯兴氏综合症，肥胖症，甲亢，肾上腺皮功能亢进，尤以其肿瘤升高显著。 降低：见于上腺皮质功能不全，如阿狄森氏病等。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
INSERT INTO public.laboratory_item (id, name, en_name, instrument_code, unit_name, clinical_significance, data_type, is_special, created_time, updated_time, deleted_time) VALUES (81, '尿17-酮类固醇 ', '17-KS ', NULL, 'mg/24h ', '增高：见于肾上腺癌，睾丸肿瘤，肾上腺皮质机能亢进。 降低：见于柯狄森氏病，性机能减退，脑下垂体前叶功能低下。 ', 2, false, '2018-05-27 00:13:28.602472+08', '2018-05-27 00:13:28.602472+08', NULL);
